import React from 'react';

class LoggedInUser extends React.Component {

    constructor(props) {
        super(props);
        this.state = { isSubscriber: 'false'};
        this.handleClick = this.handleClick.bind(this);
        //this.handleChange = this.handleChange.bind(this);
    }

    // handleChange = (event) => {

    //     const isSubscriber = this.props.isSubscriber;

    //     if (isSubscriber === false) { this.setState({ isSubscriber: true });}
    //     if (isSubscriber !== false) { this.setState({ isSubscriber: false });}

    //     const stateToUpdate = event.target.name;
    //     this.setState({ [stateToUpdate]: event.target.value })

    // }

    handleClick(event) {
        console.log("onClick körs");


        const isSubscriber = this.props.isSubscriber;

        if (isSubscriber === false) { this.setState({ isSubscriber: true });}
        if (isSubscriber !== false) { this.setState({ isSubscriber: false });}

        const stateToUpdate = event.target.name;
        this.setState({ [stateToUpdate]: event.target.value })

        //gör en put

        //skicka tillbaks state till parent
        this.props.changeStatus();


    }


    render() {

        const isSubscriber = this.props.isSubscriber;

        return(
            <div>
            <h1>
                Hej! Du är inloggad. Välkommen att prenumerera på nyhetsbrev! 
            </h1>
            <button onClick= {this.handleClick}
            onChange={this.handleChange}>
            {isSubscriber ?  'ON' : 'OFF'}
            </button>
            </div>
        );
    }
}

export default LoggedInUser;

